('en',  'l_trx_history',  'Transaction History'),
('en',  'l_trx_history_why',  'See current &amp; previous transactions.'),
('en',  'l_trx_print_footer', '<p>This electronic invoice was generated by our Order Processing System. No signature is required. A copy ofthis invoice will not accompany your package. Please retain this email.</p><p>The Shipping Charge includes any shipping and handling fees.</p><div style=\"border: solid 1px #999; padding:10px; text-align:center\"><p><b>Thank you for shopping at <a href=\"%1$s\">%2$s</a></b></p></div>'),
('en',  'l_update_cart',  'Update Cart'),
('en',  'l_update_cart_why',  'If you changed the quantity, please update cart'),
('en',  'l_view_file',  '<a href=\"viewfile.php?f=%s\" class=\"lightbox\">View File</a>'),
('en',  'l_with_tax', 'w/ Tax %s'),
('en',  'l_with_tax_guest', '-'),
('en',  'l_xe_help',  'Display Prices in Other Currency'),
('en',  'l_xpress_tag', '<br />[ <i>Express Checkout</i> ]'),
('en',  'l_zipcode',  'Zip/Postcode'),
('en',  'mail.ask_product', '<p>A visitor at your website is asking for more information regarding {$title} - <a href=\"{$site_url}/{$url}\">{$site_url}/{$url}</a></p><p style=\"border:solid 1px #999;width:290px; text-align:center; padding:5px\"><b>{$title}</b><br /><a href=\"{$site_url}/{$url}\">{$image}</a><br />{$price}</p><p>Name: {$name}<br />\r\nEmail: {$email}</p><p>The message:<br />\r\n{$tell_body}</p>'),
('en',  'mail.cancel',  '<p><b>Dear {$fullname},</b></p><p>This email is to inform you that on {$today}, we have <b>cancelled</b> your order as below:</p><p>Order Information<br><hr>\r\nOrder ID: {$order_id}<br>\r\nOrder date: {$order_date}<br>\r\nTo see your order detail, please go to <a href=\"{$site_url}/trx.php?order_id={$order_id}\">{$site_url}/trx.php?order_id={$order_id}</a></p><p>Shipping Address<br /><hr />\r\n{$ship_address}</p>'),
('en',  'mail.checkout',  '<div style=\"padding:10px; border:1px solid #888888; border-top:3px solid darkgreen; width:580px; margin:auto; background:#fff\">\r\n    <h3 align=\"right\">Order Confirmation</h3>\r\n    <p>Dear {$fullname},</p>\r\n    <p>Thank you for ordering at {$shop_name}, here are the details of your order.</p>\r\n    <table border=\"0\" cellpadding=\"3\" cellspacing=\"3\" width=\"100%\">\r\n      <tr>\r\n        <th width=\"40%\">Username</th>\r\n        <td width=\"60%\">{$current_user_id}</td>\r\n      </tr>\r\n      <tr>\r\n        <th width=\"40%\">Order ID</th>\r\n        <td width=\"60%\">{$order_id}</td>\r\n      </tr>\r\n      <tr>\r\n        <th width=\"40%\">Order Date</th>\r\n        <td width=\"60%\">{$today}</td>\r\n      </tr>\r\n      <tr>\r\n        <th width=\"40%\">Payment Method</th>\r\n        <td width=\"60%\">{$payment_method}</td>\r\n      </tr>\r\n      <tr>\r\n        <th width=\"40%\">Shipping Method</th>\r\n        <td width=\"60%\">{$shipping_method}</td>\r\n      </tr>\r\n      <tr>\r\n        <th valign=\"top\" width=\"40%\">Billing Address</th>\r\n        <td width=\"60%\">{$billing_address}</td>\r\n      </tr>\r\n      <tr>\r\n        <th valign=\"top\" width=\"40%\">Shipping Address</th>\r\n        <td width=\"60%\">{$shipping_address}</td>\r\n      </tr>\r\n    </table>\r\n    <table border=\"1\" cellpadding=\"3\" style=\"border-collapse: collapse; border-color:#999;border:solid 1px #999\" width=\"100%\">\r\n      <tr>\r\n        <th class=\"items\" width=\"50%\">Description</th>\r\n        <th class=\"items\" width=\"10%\">Qty</th>\r\n        <th class=\"items\" width=\"20%\">Unit Price</th>\r\n        <th class=\"items\" width=\"20%\">Subtotal</th>\r\n      </tr><!-- BEGINBLOCK checkout_item -->\r\n      <tr>\r\n        <td width=\"48%\">{$title}</td>\r\n        <td align=\"center\" width=\"10%\">{$qty}</td>\r\n        <td align=\"right\" width=\"19%\">{$price}</td>\r\n        <td align=\"right\" width=\"29%\">{$subtotal}</td>\r\n      </tr><!-- ENDBLOCK -->\r\n    </table>\r\n    <table border=\"0\" cellpadding=\"3\" cellspacing=\"3\" width=\"100%\">\r\n      <tr>\r\n        <th width=\"40%\">Sub Total</th>\r\n        <td width=\"60%\">{$total}</td>\r\n      </tr>\r\n      <tr>\r\n        <th width=\"40%\">Coupon Discount</th>\r\n        <td width=\"60%\">{$coupon_disc} {$gift_code}</td>\r\n      </tr>\r\n      <tr>\r\n        <th width=\"40%\">Shipping Fee</th>\r\n        <td width=\"60%\">{$shipping_cost}</td>\r\n      </tr>\r\n      <tr>\r\n        <th width=\"40%\">Payment Fee</th>\r\n        <td width=\"60%\">{$payment_cost}</td>\r\n      </tr>\r\n      <tr>\r\n        <th width=\"40%\">Tax</th>\r\n        <td width=\"60%\">{$tax}</td>\r\n      </tr>\r\n      <tr>\r\n        <th width=\"40%\">Total</th>\r\n        <td width=\"60%\">{$grand_total}</td>\r\n      </tr>\r\n    </table><!-- BEGINIF $howtopay -->\r\n    <div class=\"section\">\r\n      <p>How to Pay Information</p>\r\n    </div>\r\n    <p>You have selected {$payment_method} for payment method. Please read the following information on how to pay for your orders.</p>{$howtopay}<!-- ENDIF -->\r\n    <div class=\"section\">\r\n      <p>Thank you for shopping at <a href=\"{$site_url}\">{$shop_name}</a></p>\r\n    </div>\r\n    <p>This order confirmation has been automatically generated by our order tracking system.</p>\r\n    <p>Should you have any questions about your order, please visit the following url for updated order information: <a href=\"{$trx_check_url}\">{$trx_check_url}</a></p>\r\n    <p>Yours sincerely,<br>\r\n    Customer Service Department<br>\r\n    <b>{$shop_name}</b><br>\r\n    {$shop_address}</p>\r\n  </div>'),
('en',  'mail.checkout_admin',  '<p>You have just got a new order!</p>\r\n<h3>Order Information</h3>\r\n<p><b>Order ID:</b> {$order_id}<br /><b>Order date:</b> {$today}</p><h3>Products</h3><ul><!-- BEGINBLOCK checkout_item_adm --><li>{$where_am_i}: {$qty} x {$title} = {$subtotal}</li><!-- ENDBLOCK --></ul><p><b>Subtotal:</b> {$total}<br /><b>Coupon discount:</b> {$coupon_disc} {$gift_code}<br /><b>Tax:</b> {$tax}<br /><b>Shipping fee:</b> {$shipping_cost}<br /><b>Payment fee:</b> {$payment_cost}<br /><b>Total:</b> {$grand_total}</b></p><h3>Shipping Address</h3><p><b>{$fullname}</b><br />\r\n{$shipping_address}</p><h3>Payment Information</h3><p><b>Selected method of payment:</b> {$payment_method}<br /><b>Payment status:</b> {$payment_status}</p><p>If this order paid using PayPal IPN, be sure to recheck the PayPal log manually from your ACP.</p><p>You can manage this transaction at: <a href=\"{$adm_check_url}\">{$adm_check_url}</a></p>'),
('en',  'mail.coupon',  '<b>Hello there!</b><br>\r\nThis email contains an exclusive gift coupon for use at our website!<br><blockquote><b>Coupon code:</b> {{{$gift_code}}}<br><b>Gift value:</b> {{{$gift_value}}}<br><b>Minimum purchase:</b> {{{$min_purchase}}}<br><b>Valid until:</b> {{{$valid_date}}}<br></blockquote><p>To redeem your coupon:</p><ol><li>Locate the products and add them to your cart.</li><li>Whilst placing your order, enter the coupon code when prompted.</li><li>Your coupon will automatically deduct any savings from your order total.</li></ol><p>Thank you.</p>\r\n{$site_name} - {$site_url}'),
('en',  'mail.digital', '<p><b>Hello {$fullname},</b></p><p>This email is to inform you that we have sent your digital products.</p><ul><!-- BEGINBLOCK list --><li>{$title}</li><!-- ENDBLOCK --></ul><p>All digital products can be downloaded in your control panel.</p><ol><li>Login using your username &amp; password at our site.</li><li>Go to My Account &gt; My Files.</li><li>All purchased  files are listed there.</li></ol><p>Thank you for doing bussiness with us! :)</p>'),
('en',  'mail.notify',  'Hi,<!-- BEGINIF $sub_type == \'rst\' -->We are pleased to inform you that \"{$title}\" is now available!<!-- ENDIF --><!-- BEGINIF $sub_type == \'pcd\' -->We are pleased to tell you that \"{$title}\" has droped in price! It\'s now only {$price}!<!-- ENDIF -->\r\n\r\nYou can see the product at our site {$site_name}, {$site_url}/detail.php?item_id={$item_id}\r\n\r\n---\r\nYou have received this email because you subscribed for notifications at {$site_name}, {$site_url}\r\nTo manage your subscriptions please go to {$site_url}/account.php and login.'),
('en',  'mail.pending', '<p><b>Hello {$fullname},</b></p><p>This email is to inform you that on {$today}, we set your order status back to pending:</p><p>Order Information<br /><hr />\r\nOrder ID: {$order_id}<br />\r\nOrder date: {$order_date}<br />\r\nTo see your order details, please go to <a href=\"{$site_url}/trx.php?order_id={$order_id}\">{$site_url}/trx.php?order_id={$order_id}</a></p>'),
('en',  'mail.process', '<p><b>Hello {$fullname},</b></p><p>This email is to inform you that on {$today}, we are processing your order as below:</p><p>Order Information<br /><hr />\r\nOrder ID: {$order_id}<br />\r\nOrder date: {$order_date}<br />\r\nTo see your order details, please go to <a href=\"{$site_url}/trx.php?order_id={$order_id}\">{$site_url}/trx.php?order_id={$order_id}</a></p><p>Digital Products<br /><hr />\r\nAll digital products can be downloaded in your control panel.</p><ol><li>Login using your username &amp; password at our site.</li><li>Go to My Account &gt; My Files.</li><li>All purchased  files are listed there.</li></ol>'),
('en',  'mail.ship',  '<p><b>Hello {$fullname},</b></p><p>This email is to inform you that on {$today}, we have shipped your order as below:</p><p>Order Information<br /><hr />\r\nOrder ID: {$order_id}<br />\r\nOrder date: {$order_date}<br />\r\nTo see your order details, please go to <a href=\"{$site_url}/trx.php?order_id={$order_id}\">{$site_url}/trx.php?order_id={$order_id}</a></p><p>Shipping Address<br /><hr />\r\n{$ship_address}</p><p>Digital Products<br /><hr />\r\nAll digital products can be downloaded in your control panel.</p><ol><li>Login using your username &amp; password at our site.</li><li>Go to My Account &gt; My Files.</li><li>All purchased  files are listed there.</li></ol>'),
('en',  'mail.tell_product',  '<p>Hi, <b>{$friend_name}</b>,</p><p>Your friend, {$name} (email: {$email}) thinks that you might be interested in <i>{$title}</i> in our online shop.</p><p>For more information, visit <a href=\"{$site_url}/{$url}\">{$site_url}/{$url}</a>.</p><p style=\"border:solid 1px #999;width:290px; text-align:center; padding:5px\"><b>{$title}</b><br /><a href=\"{$site_url}/{$url}\">{$image}</a><br />{$price}</p><p>Additonally, your friend says:<br />\r\n{$tell_body}</p><p>Best Regards,<br />\r\n{$site_name}<br /><a href=\"{$site_url}\">{$site_url}</a></p>'),
('en',  'mail.tell_us', '<p>Hi, <b>site owner</b>,</p><p>A visitor, {$name} (email: {$email}) sent you a message about a product in your web site of \"{$title}\".</p><p style=\"border:solid 1px #999;width:290px; text-align:center; padding:5px\"><a href=\"{$site_url}/{$url}\"><b>{$title}</b></a><br /><a href=\"{$site_url}/{$url}\">{$image}</a><br />{$details}</p><p>The message is as follow:<br />{$tell_body}</p><p>To answer this message, please contact the visitor directly.</p><p>Best Regards,<br />{$site_name}<br /><a href=\"{$site_url}\">{$site_url}</a></p>'),
('en',  'msg.cancel_not_allow', '<h1>Whoops!</h1><p>Sorry! We can\'t cancel your order at present. Please contact us for more information</p>'),
('en',  'msg.cancel_ok',  '<h1>Order Cancellation Sent!</h1><p>Your request for cancellation has been sent. We will review your request as soon as possible.</p>'),
('en',  'msg.coupon_err', '<h1>Invalid Coupon Code!</h1><p>Invalid coupon code! It may already have expired, been claimed or it could be an incorrect coupon code altogether!</p>'),
('en',  'msg.coupon_ok',  '<h1>Gift Coupon!</h1><p>%1$s</p>'),
('en',  'msg.courier_not_set',  '<h1>Failed!</h1><p>You have entered an unrecognized city, or we do not cover the city which you have just entered.</p>'),
('en',  'msg.internal_error', '<h1>Internal Error</h1><p><p>%1$s</p><p>Please contact site administrator!</p></p>'),
('en',  'msg.item_id_not_found',  '<h1>Product Not Found</h1><p>Product is not availble or no longer available!</p>'),
('en',  'msg.not_queued', '<h1>Unable to Cancel the Order!</h1><p>Current order is no longer queued. You cannot cancel it.</p>'),
('en',  'msg.no_file',  '<h1>File Not Found!</h1><p>File does not exist, or not yours. Please contact us for more information.</p>'),
('en',  'msg.no_item',  '<h1>No items to Display</h1><p>Your query doesn\'t return anything. Please try again.</p>'),
('en',  'msg.no_item_in_cart',  '<h1>Unable to Continue!</h1><p>There are no items in your shopping cart!</p>'),
('en',  'msg.no_stock', '<h1>Out of Stock</h1><p></p>'),
('en',  'msg.order_id_not_found', '<h1>Order ID Not Found</h1><p>Order ID can not be found or is no longer available!</p>'),
('en',  'msg.order_id_null',  '<h1>Order ID Not Set</h1><p>To check your order status, please input an order ID.</p>'),
('en',  'msg.payment_not_selected', '<h1>Checkout Failed!</h1><p>You have not chosen a method of payment. Please return to the previous page and try again</p>'),
('en',  'msg.shipper_not_selected', '<h1>Whoops!</h1><p>You have not chosen a shipping method. Please return to the previous page and try again.</p>'),
('en',  'msg.sub_type_err', '<h1>Unknown Subscription Method!</h1><p>Please contact us for more information</p>'),
('en',  'msg.tax_info', '<h1>Tax Settings</h1><p>To set tax, go to Country/State/City administration.<br />There you can set tax based on a specific country, county / state or even city!</p>');
