

DROP TABLE IF EXISTS `__PREFIX__gift`;
CREATE TABLE `__PREFIX__gift` (
  `idx` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `gift_code` varchar(32) NOT NULL,
  `master_code` varchar(25) NOT NULL,
  `gift_value` decimal(10,2) NOT NULL DEFAULT '0.00',
  `gift_pct` enum('0','1') NOT NULL DEFAULT '0',
  `min_purchase` decimal(10,2) NOT NULL DEFAULT '0.00',
  `valid_date` date NOT NULL DEFAULT '0000-00-00',
  `coupon_type` enum('once','multi') NOT NULL DEFAULT 'once',
  `redeem_email` varchar(255) NOT NULL,
  `redeem_msg` text NOT NULL,
  `redeem_sent` enum('0','1') NOT NULL DEFAULT '0',
  `redeem_order_id` varchar(16) NOT NULL,
  `redeem_user_id` varchar(80) NOT NULL,
  `redeem_date` int(10) unsigned NOT NULL,
  `redeem_purchase` decimal(10,2) NOT NULL,
  PRIMARY KEY (`idx`),
  UNIQUE KEY `gift_code` (`gift_code`),
  KEY `master_code` (`master_code`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;



TRUNCATE TABLE `__PREFIX__menu_item`;


TRUNCATE TABLE `__PREFIX__menu_set`;




DROP TABLE IF EXISTS `__PREFIX__orders`;
CREATE TABLE `__PREFIX__orders` (
  `idx` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `user_id` varchar(80) NOT NULL DEFAULT '',
  `item_id` int(10) unsigned NOT NULL DEFAULT '0',
  `qty` tinyint(3) unsigned NOT NULL DEFAULT '0',
  `timeadd` int(10) unsigned NOT NULL DEFAULT '0',
  PRIMARY KEY (`idx`),
  KEY `user_id` (`user_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='Temporary order, later moved to order_final';


DROP TABLE IF EXISTS `__PREFIX__order_final`;
CREATE TABLE `__PREFIX__order_final` (
  `idx` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `user_id` varchar(80) NOT NULL DEFAULT '',
  `cat_id` int(10) unsigned NOT NULL DEFAULT '0',
  `item_id` int(10) unsigned NOT NULL DEFAULT '0',
  `title` varchar(255) NOT NULL DEFAULT '',
  `weight` float NOT NULL DEFAULT '0',
  `price` decimal(10,2) NOT NULL DEFAULT '0.00',
  `qty` tinyint(3) unsigned NOT NULL DEFAULT '0',
  `order_id` varchar(16) NOT NULL DEFAULT '',
  PRIMARY KEY (`idx`),
  KEY `user_id` (`user_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='Final order';


DROP TABLE IF EXISTS `__PREFIX__order_summary`;
CREATE TABLE `__PREFIX__order_summary` (
  `idx` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `order_id` varchar(16) NOT NULL DEFAULT '',
  `user_id` varchar(80) NOT NULL DEFAULT '',
  `user_email` varchar(255) NOT NULL,
  `order_items` int(10) unsigned NOT NULL,
  `order_weight` float unsigned NOT NULL,
  `order_date` date NOT NULL DEFAULT '0000-00-00',
  `order_total` decimal(10,2) NOT NULL DEFAULT '0.00',
  `order_shipping_fee` decimal(10,2) NOT NULL DEFAULT '0.00',
  `order_payment_fee` decimal(10,2) NOT NULL DEFAULT '0.00',
  `order_tax` decimal(10,2) NOT NULL DEFAULT '0.00',
  `gift_discount` decimal(10,2) NOT NULL DEFAULT '0.00',
  `order_payment` varchar(80) NOT NULL DEFAULT '',
  `order_shipper` varchar(80) NOT NULL DEFAULT '',
  `order_paystat` char(1) NOT NULL DEFAULT 'E',
  `order_status` char(1) NOT NULL DEFAULT 'E',
  `order_shipped` date NOT NULL DEFAULT '0000-00-00',
  `order_delivered` date NOT NULL,
  `order_completed` date NOT NULL,
  `order_cancelled` date NOT NULL,
  `fullname` varchar(255) NOT NULL,
  `bill_address` text NOT NULL,
  `ship_address` text NOT NULL,
  `order_notes` text NOT NULL,
  `xpress_co` enum('0','1') NOT NULL DEFAULT '0',
  PRIMARY KEY (`order_id`),
  UNIQUE KEY `idx` (`idx`),
  KEY `order_date` (`order_date`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 PACK_KEYS=0 COMMENT='Order summary';




DROP TABLE IF EXISTS `__PREFIX__payment_log`;
CREATE TABLE `__PREFIX__payment_log` (
  `log_id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `log_time` int(10) unsigned NOT NULL,
  `pay_type` varchar(20) NOT NULL,
  `order_id` varchar(16) NOT NULL,
  `txn_id` varchar(32) NOT NULL,
  `order_total` decimal(10,2) NOT NULL,
  `order_paid` decimal(10,2) NOT NULL,
  `sent_request` text NOT NULL,
  `response` text NOT NULL,
  `payment_status` char(1) NOT NULL,
  `notes` text NOT NULL,
  PRIMARY KEY (`log_id`),
  KEY `order_id` (`order_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `__PREFIX__products`;
CREATE TABLE `__PREFIX__products` (
  `cat_id` varchar(12) NOT NULL DEFAULT '',
  `idx` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `sku` varchar(16) NOT NULL DEFAULT '',
  `add_category` varchar(255) NOT NULL DEFAULT '',
  `permalink` varchar(255) NOT NULL,
  `title` varchar(255) NOT NULL DEFAULT '',
  `price` decimal(10,2) unsigned NOT NULL DEFAULT '0.00',
  `price_msrp` decimal(10,2) unsigned NOT NULL DEFAULT '0.00',
  `price_qty` varchar(255) NOT NULL,
  `details` text NOT NULL,
  `weight` decimal(5,2) unsigned NOT NULL DEFAULT '0.00',
  `distro` int(10) unsigned NOT NULL,
  `list_date` date NOT NULL DEFAULT '0000-00-00',
  `keywords` varchar(255) NOT NULL DEFAULT '',
  `stock` int(10) unsigned NOT NULL DEFAULT '0',
  `min_buy` int(10) unsigned NOT NULL,
  `max_buy` int(10) unsigned NOT NULL,
  `tax_class` int(10) unsigned NOT NULL DEFAULT '0',
  `see_also` varchar(255) NOT NULL DEFAULT '',
  `sub_product` text NOT NULL,
  `digital_file` varchar(255) NOT NULL,
  `is_call_for_price` enum('0','1') NOT NULL DEFAULT '0',
  `is_invisible` enum('0','1') NOT NULL DEFAULT '0',
  `stat_hits` int(10) unsigned NOT NULL DEFAULT '0',
  `stat_last_hit` date NOT NULL DEFAULT '0000-00-00',
  `stat_purchased` int(10) unsigned NOT NULL,
  `stat_last_purchased` date NOT NULL,
  `smart_search` text NOT NULL,
  PRIMARY KEY (`idx`),
  KEY `cat_id` (`cat_id`),
  KEY `sku` (`sku`),
  KEY `add_category` (`add_category`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 PACK_KEYS=0 COMMENT='Product list';

DROP TABLE IF EXISTS `__PREFIX__product_cat`;
CREATE TABLE `__PREFIX__product_cat` (
  `parent_id` int(10) unsigned NOT NULL DEFAULT '0',
  `idx` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `cat_name` varchar(100) NOT NULL DEFAULT '',
  `cat_details` text NOT NULL,
  `cat_image` varchar(255) NOT NULL DEFAULT '',
  `cat_keywords` varchar(255) NOT NULL,
  `cat_featured` varchar(255) NOT NULL DEFAULT '0',
  `cat_page` varchar(10) NOT NULL,
  `menu_mid` int(10) unsigned NOT NULL,
  `permalink` varchar(255) NOT NULL,
  `subcat_order` varchar(255) NOT NULL,
  PRIMARY KEY (`idx`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `__PREFIX__product_cf_define`;
CREATE TABLE `__PREFIX__product_cf_define` (
  `idx` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `cf_category` varchar(255) NOT NULL,
  `cf_title` varchar(255) NOT NULL,
  `cf_type` varchar(10) NOT NULL,
  `cf_option` text NOT NULL,
  `cf_help` varchar(255) NOT NULL,
  `is_searchable` enum('0','1') NOT NULL DEFAULT '0',
  `is_list` enum('0','1') NOT NULL DEFAULT '0',
  `menu_idx` int(10) unsigned NOT NULL,
  `menu_item_id` int(10) unsigned NOT NULL,
  `is_removed` enum('0','1') NOT NULL DEFAULT '0',
  PRIMARY KEY (`idx`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS `__PREFIX__product_cf_value`;
CREATE TABLE `__PREFIX__product_cf_value` (
  `idx` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `item_id` int(10) unsigned NOT NULL,
  PRIMARY KEY (`idx`),
  KEY `item_id` (`item_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


ALTER TABLE `__PREFIX__user`
ADD `fullname` varchar(100) NOT NULL DEFAULT '' AFTER `user_notes`,
ADD `bill_address` varchar(255) NOT NULL DEFAULT '',
ADD `bill_address2` varchar(255) NOT NULL DEFAULT '',
ADD `bill_district` varchar(255) NOT NULL,
ADD `bill_city` varchar(255) NOT NULL DEFAULT '',
ADD `bill_state` varchar(80) NOT NULL DEFAULT '',
ADD `bill_country` varchar(80) NOT NULL DEFAULT '',
ADD `bill_zip` varchar(15) NOT NULL DEFAULT '',
ADD `ship_address` varchar(255) NOT NULL,
ADD `ship_address2` varchar(255) NOT NULL,
ADD `ship_district` varchar(255) NOT NULL,
ADD `ship_city` varchar(255) NOT NULL,
ADD `ship_state` varchar(80) NOT NULL,
ADD `ship_country` varchar(80) NOT NULL,
ADD `ship_zip` varchar(15) NOT NULL,
ADD `phone` varchar(20) NOT NULL DEFAULT '',
ADD `user_wish` text NOT NULL;


DROP TABLE IF EXISTS `__PREFIX__user_file`;
CREATE TABLE `__PREFIX__user_file` (
  `idx` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `user_id` varchar(80) NOT NULL,
  `item_id` int(10) unsigned NOT NULL,
  `order_idx` varchar(16) NOT NULL,
  `total_download` int(10) unsigned NOT NULL,
  `last_download` int(10) unsigned NOT NULL,
  PRIMARY KEY (`idx`),
  KEY `user_id` (`user_id`),
  KEY `item_id` (`item_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

